  <span class="pl-c1">issuePrefixes</span>: <span class="pl-kos">[</span><span class="pl-s">'TEST-'</span><span class="pl-kos">]</span><span class="pl-kos">,</span>
  <span class="pl-c1">issueUrlFormat</span>: <span class="pl-s">'https://myBugTracker.com/{{prefix}}{{id}}'</span>
<span class="pl-kos">}</span><span class="pl-kos">)</span><span class="pl-kos">.</span><span class="pl-en">then</span><span class="pl-kos">(</span><span class="pl-kos">(</span><span class="pl-s1">config</span><span class="pl-kos">)</span> <span class="pl-c1">=&gt;</span> <span class="pl-kos">{</span>
