    &quot;preset&quot;: {
      &quot;name&quot;: &quot;conventionalchangelog&quot;,
      &quot;issuePrefixes&quot;: [&quot;TEST-&quot;],
      &quot;issueUrlFormat&quot;: &quot;https://myBugTracker.com/{{prefix}}{{id}}&quot;
