  <span class="pl-ent">"options"</span>: {
    <span class="pl-ent">"preset"</span>: {
      <span class="pl-ent">"name"</span>: <span class="pl-s"><span class="pl-pds">"</span>conventionalchangelog<span class="pl-pds">"</span></span>,
      <span class="pl-ent">"issuePrefixes"</span>: [<span class="pl-s"><span class="pl-pds">"</span>TEST-<span class="pl-pds">"</span></span>],
      <span class="pl-ent">"issueUrlFormat"</span>: <span class="pl-s"><span class="pl-pds">"</span>https://myBugTracker.com/{{prefix}}{{id}}<span class="pl-pds">"</span></span>
