<span class="pl-k">const</span> <span class="pl-s1">tree</span> <span class="pl-c1">=</span> <span class="pl-en">unified</span><span class="pl-kos">(</span><span class="pl-kos">)</span><span class="pl-kos">.</span><span class="pl-en">use</span><span class="pl-kos">(</span><span class="pl-s1">remarkParse</span><span class="pl-kos">)</span><span class="pl-kos">.</span><span class="pl-en">parse</span><span class="pl-kos">(</span><span class="pl-s1">doc</span><span class="pl-kos">)</span><span class="pl-kos">;</span>

