	<span class="pl-c1">padding-left</span>: <span class="pl-c1">20</span><span class="pl-k">px</span>;
	<span class="pl-c1">margin-top</span>: auto;
	<span class="pl-c1">margin-bottom</span>: auto;
	<span class="pl-c1">border-right-width</span>: <span class="pl-c1">2</span><span class="pl-k">px</span>;
