	postcssNesting(/* pluginOptions */)
]).process(YOUR_CSS /*, processOptions */);"><pre><span class="pl-k">const</span> <span class="pl-s1">postcss</span> <span class="pl-c1">=</span> <span class="pl-en">require</span><span class="pl-kos">(</span><span class="pl-s">'postcss'</span><span class="pl-kos">)</span><span class="pl-kos">;</span>
<span class="pl-k">const</span> <span class="pl-s1">postcssNesting</span> <span class="pl-c1">=</span> <span class="pl-en">require</span><span class="pl-kos">(</span><span class="pl-s">'postcss-nesting'</span><span class="pl-kos">)</span><span class="pl-kos">;</span>

<span class="pl-s1">postcss</span><span class="pl-kos">(</span><span class="pl-kos">[</span>
	<span class="pl-s1">postcssNesting</span><span class="pl-kos">(</span><span class="pl-c">/* pluginOptions */</span><span class="pl-kos">)</span>
<span class="pl-kos">]</span><span class="pl-kos">)</span><span class="pl-kos">.</span><span class="pl-en">process</span><span class="pl-kos">(</span><span class="pl-c1">YOUR_CSS</span> <span class="pl-c">/*, processOptions */</span><span class="pl-kos">)</span><span class="pl-kos">;</span></pre></div>
