  <span class="pl-kos">&lt;</span><span class="pl-ent">awesome-element</span> <span class="pl-c1">id</span>="<span class="pl-s">fixture</span>"<span class="pl-kos">&gt;</span><span class="pl-kos">&lt;/</span><span class="pl-ent">awesome-element</span><span class="pl-kos">&gt;</span>
