        <span class="pl-s1">assert</span><span class="pl-kos">.</span><span class="pl-en">isTrue</span><span class="pl-kos">(</span><span class="pl-smi">document</span><span class="pl-kos">.</span><span class="pl-en">getElementById</span><span class="pl-kos">(</span><span class="pl-s">'fixture'</span><span class="pl-kos">)</span><span class="pl-kos">.</span><span class="pl-c1">awesomest</span><span class="pl-kos">)</span><span class="pl-kos">;</span>
